package project.stutisrivastava.learningtousevolley.util;import android.app.AlertDialog;import android.content.Context;import android.content.DialogInterface;import project.stutisrivastava.learningtousevolley.listener.ConfirmationListener;public class Alert {	public static void showInfo(Context instance, String title, String message,			String buttonTitle) {		AlertDialog alertDialog = new AlertDialog.Builder(instance).create();		alertDialog.setTitle(title);		alertDialog.setCanceledOnTouchOutside(false);		alertDialog.setMessage(message);		alertDialog.setButton(AlertDialog.BUTTON_POSITIVE,buttonTitle,				new DialogInterface.OnClickListener() {					public void onClick(DialogInterface dialog, int which) {						dialog.dismiss();						SystemManager.getCurrentActivity().finish();					}				});		alertDialog.show();	}	public static void showConfirmationDialog(Context instance,			final ConfirmationListener listener, String title, String message) {		{			AlertDialog alertDialog = new AlertDialog.Builder(instance)					.create();			alertDialog.setTitle(title);			alertDialog.setMessage(message);			alertDialog.setCanceledOnTouchOutside(false);			alertDialog.setButton(AlertDialog.BUTTON_POSITIVE, "OK",					new DialogInterface.OnClickListener() {						public void onClick(DialogInterface dialog, int which) {							listener.onConfirmationSet(true);							dialog.dismiss();						}					});			alertDialog.setButton(AlertDialog.BUTTON_NEGATIVE, "Cancel",					new DialogInterface.OnClickListener() {						public void onClick(DialogInterface dialog, int which) {							listener.onConfirmationSet(false);							dialog.dismiss();						}					});			alertDialog.show();		}	}}